function click(){var e=document.getElementById("xValues").value;var t=document.getElementById("yValues").value;var a=document.getElementById("myChart").getContext("2d");var n=document.getElementById("message");n.innerHTML="Generating Graph...";try{var r=e.split(",");var o=t.split(",");if(r.length!=o.length){throw"Wrong number of values."}var l=[];for(i=0;i<r.length;i++){var s={};var d=parseInt(r[i]);var c=parseInt(o[i]);if(isNaN(d)||isNaN(c)){throw"Invalid values: ("+r[i]+", "+o[i]+")."}s.x=d;s.y=c;l.push(s)}var u=new Chart(a,{type:"scatter",data:{datasets:[{label:"Scatter Data",data:l,fill:false,pointRadius:5,pointBorderWidth:2,pointBackgroundColor:"rgba(146, 211, 146, 0.4)",pointBorderColor:"rgba(0, 58, 0, 0.9)"}]},options:{scales:{xAxes:[{type:"linear",position:"bottom"}]},showLines:false}})}catch(e){n.innerHTML=e}finally{}}document.addEventListener("DOMContentLoaded",function(){document.getElementById("generateGraph").addEventListener("click",click)});